import React from "react";

const Card =(pokemon,loading)=>{
    console.log(pokemon);
    return (
        <>
        {
            loading ? <h1>Loading...</h1> :
            pokemon.map((item)=>{
                return(
                    <>
                    <div className="card">
                        <h2>{item.id}</h2>
                        <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/back/shiny/4.png" alt="" />
                        <h1>Charmander</h1>
                    </div>
                    </>
                )
            })
        }

        </>
    )
}
export default Card;
